import React, { useEffect, useState, Component } from "react";
import moment from "moment";
import { Container, ImageGroup, Divider, Button } from "semantic-ui-react";
import { StyledCard } from "./styles";

function DEF(props) {
  return (
    <div>
      <h1>{props.text}</h1>
    </div>
  );
}

function Time() {
  const [days, setDays] = useState(0);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    setInterval(() => {
      const now = moment();
      const then = moment("2022-01-01 12:12:12", "YYYY-MM-DD hh:mm:ss");
      const countdown = moment(then - now);
      setDays(countdown.format("D"));
      setHours(countdown.format("HH"));
      setMinutes(countdown.format("mm"));
      setSeconds(countdown.format("ss"));
    }, 1000);
  }, []);

  return (
    <Container
      style={{
        color: "white",
        textAlign: "center",
      }}>
      <StyledCard>{days}"&nbsp;"</StyledCard>
      <StyledCard>{hours}</StyledCard>
      <StyledCard>{minutes}</StyledCard>
      <StyledCard>{seconds}</StyledCard>
      <DEF text="2" />
    </Container>
  );
}

export default Time;
